{"output":"import { AntDesign, Feather, Ionicons, MaterialIcons } from '@expo/vector-icons';
import type React from 'react';
import { Switch, Text, TouchableOpacity, type TouchableOpacityProps, View } from 'react-native';
import { cn } from '@/utils/cn';
import { indicatorColors } from '@/utils/styleUtils';

type IconLibrary = 'MaterialIcons' | 'Ionicons' | 'Feather' | 'AntDesign';

type LeadingElement =
  | {
      type: 'icon';
      library: IconLibrary;
      name: string;
      size?: number;
      color?: string;
      className?: string;
    }
  | { type: 'text'; content: string; className?: string }
  | {
      type: 'avatar';
      initials: string;
      backgroundColor?: string;
      textColor?: string;
      size?: 'small' | 'medium' | 'large';
    }
  | {
      type: 'badge';
      count: number;
      backgroundColor?: string;
      textColor?: string;
      maxCount?: number;
    };

type TrailingElement =
  | { type: 'text'; content: string; className?: string }
  | { type: 'switch'; value: boolean; onValueChange: (value: boolean) => void; disabled?: boolean }
  | {
      type: 'icon';
      library: IconLibrary;
      name: string;
      size?: number;
      color?: string;
      className?: string;
    }
  | {
      type: 'badge';
      count: number;
      backgroundColor?: string;
      textColor?: string;
      maxCount?: number;
    }
  | { type: 'progress'; value: number; max?: number; showPercentage?: boolean; className?: string }
  | { type: 'status'; variant: 'online' | 'offline' | 'away' | 'busy'; label?: string };

interface RowProps extends TouchableOpacityProps {
  title: string;
  titleClassName?: string;
  subtitle?: string;
  subtitleClassName?: string;
  leading?: LeadingElement;
  trailing?: TrailingElement;
  showCaret?: boolean;
  caretClassName?: string;
  className?: string;
  contentClassName?: string;
}

export const Row: React.FC<RowProps> = ({
  title,
  titleClassName,
  subtitle,
  subtitleClassName,
  leading,
  trailing,
  showCaret = false,
  caretClassName,
  className,
  contentClassName,
  ...props
}) => {
  const Component = props.onPress ? TouchableOpacity : View;

  const baseClasses = 'flex-row items-center py-3 px-4';
  const rowClasses = cn(baseClasses, className);

  const defaultTitleClasses = 'text-base font-medium text-foreground';
  const defaultSubtitleClasses = 'text-sm text-muted-foreground mt-1';
  const defaultCaretClasses = 'text-muted-foreground text-lg';

  const titleClasses = cn(defaultTitleClasses, titleClassName);
  const subtitleClasses = cn(defaultSubtitleClasses, subtitleClassName);
  const caretClasses = cn(defaultCaretClasses, caretClassName);

  const renderIcon = (params: {
    library: IconLibrary;
    name: string;
    size?: number;
    color?: string;
    className?: string;
  }) => {
    const { library, name, size = 20, color = '#666666', className } = params; // Using muted-foreground equivalent

    const iconElement = (() => {
      switch (library) {
        case 'MaterialIcons':
          return (
            <MaterialIcons
              name={name as React.ComponentProps<typeof MaterialIcons>['name']}
              size={size}
              color={color}
            />
          );
        case 'Ionicons':
          return (
            <Ionicons
              name={name as React.ComponentProps<typeof Ionicons>['name']}
              size={size}
              color={color}
            />
          );
        case 'Feather':
          return (
            <Feather
              name={name as React.ComponentProps<typeof Feather>['name']}
              size={size}
              color={color}
            />
          );
        case 'AntDesign':
          return (
            <AntDesign
              name={name as React.ComponentProps<typeof AntDesign>['name']}
              size={size}
              color={color}
            />
          );
        default:
          return (
            <MaterialIcons
              name={name as React.ComponentProps<typeof MaterialIcons>['name']}
              size={size}
              color={color}
            />
          );
      }
    })();

    return className ? <View className={className}>{iconElement}</View> : iconElement;
  };

  const renderAvatar = (params: {
    initials: string;
    backgroundColor?: string;
    textColor?: string;
    size?: 'small' | 'medium' | 'large';
  }) => {
    const {
      initials,
      backgroundColor = indicatorColors.primary,
      textColor = 'white',
      size = 'medium',
    } = params;

    const sizeClasses = {
      small: 'w-6 h-6 text-xs',
      medium: 'w-8 h-8 text-sm',
      large: 'w-10 h-10 text-base',
    };

    return (
      <View
        className={cn('rounded-full flex items-center justify-center', sizeClasses[size])}
        style={{ backgroundColor }}
      >
        <Text className="font-semibold" style={{ color: textColor }}>
          {initials.slice(0, 2).toUpperCase()}
        </Text>
      </View>
    );
  };
